<div class="flex justify-center items-center space-x-20" style="
    margin-top: 12%;
">
  <div class="w-1/2 mt-20" style="overflow-y: scroll; max-height: 600px; max-width: 900px;">
    <div *ngFor="let item of boxes; let i = index" class="mb-4">
      <div class="font-bold text-2xl">
        <img ngSrc="{{item.img}}" fill="" width="300"> alt="{{item.nom}}"/>{{ item.nom }} {{ item.prix }}€
      </div>
      <div class="text-xl">Saveurs:</div>
      <ul>
        <li *ngFor="let saveur of item.saveurs.split(', ')">
          {{ saveur }}
        </li>
      </ul>
      <div>
        <button (click)="incrementAndDisplay(item.id)" class="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
          +
        </button>
        <button (click)="decrementAndDisplay(item.id)" class="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded">
          -
        </button>
      </div>
    </div>
  </div>
  <div class="w-1/2">
    <div id="boxName" class="w-1/2"></div>
    <div style="font-weight: bold;"id="prixTotal"></div>
  </div>
</div>
