import { Component } from '@angular/core';
import { BoxesService } from 'src/app/Services/boxes.service';
import { OnInit } from '@angular/core';
import { HttpClientModule } from '@angular/common/http';

@Component({
  selector: 'app-list-box',
  templateUrl: './list-box.component.html',
  styleUrls: ['./list-box.component.css']
})
export class ListBoxComponent implements OnInit {
  boxes: any;

  constructor(private boxesService: BoxesService) { }

  ngOnInit(): void {
    this.boxesService.fetchData().subscribe((data: any) => {
      this.boxes = data;
    });
  }

}
  <div *ngFor="let item of boxes | keyvalue">
    <div style="font-weight: bold; font-size:32px">

      {{ item.value }} {{ item.value }}€
    </div>
    <div style="font-size:22px;">Saveurs:</div>
    <div *ngFor="let saveur of item.value.saveurs.split(', ')">
      {{ saveur }}
    </div>
  </div>
